<script>
import Vue from 'vue'
import dayjs from 'dayjs'
import Canvg from 'canvg'

export default {
  name: 'Home',
  data () {
    return {
      background: '#ffffff',
      transparent: 'no',
      options: [
        { text: 'Yes', value: 'yes' },
        { text: 'No', value: 'no' }
      ],
      body: 'BodyTee1',
      head: 'HeadAfro',
      face: 'FaceSmile',
      accessory: null,
      facial: null
    }
  },
  computed: {
    transparentOption () {
      return this.transparent === 'yes'
    },
    accessoryOptions () {
      const options = [
        {
          value: null,
          text: 'None'
        }
      ]
      return [
        ...options,
        ...Object.keys(Vue.options.components)
          .filter(item => {
            return item.startsWith('Accessory')
          })
          .map(item => {
            return {
              value: item,
              text: item
            }
          })
      ]
    },
    bodyOptions () {
      return Object.keys(Vue.options.components)
        .filter(item => {
          return item.startsWith('Body')
        })
        .map(item => {
          return {
            value: item,
            text: item
          }
        })
    },
    facialOptions () {
      const options = [
        {
          value: null,
          text: 'None'
        }
      ]
      return [
        ...options,
        ...Object.keys(Vue.options.components)
          .filter(item => {
            return item.startsWith('Facial')
          })
          .map(item => {
            return {
              value: item,
              text: item
            }
          })
      ]
    },
    headOptions () {
      return Object.keys(Vue.options.components)
        .filter(item => {
          return item.startsWith('Head')
        })
        .map(item => {
          return {
            value: item,
            text: item
          }
        })
    },
    faceOptions () {
      return Object.keys(Vue.options.components)
        .filter(item => {
          return item.startsWith('Face')
        })
        .map(item => {
          return {
            value: item,
            text: item
          }
        })
    }
  },
  methods: {
    generateRandom () {
      this.head = this.headOptions[Math.floor(Math.random() * this.headOptions.length)].value
      this.body = this.bodyOptions[Math.floor(Math.random() * this.bodyOptions.length)].value
      this.accessory = this.accessoryOptions[Math.floor(Math.random() * this.accessoryOptions.length)].value
      this.facial = this.facialOptions[Math.floor(Math.random() * this.facialOptions.length)].value
      this.face = this.faceOptions[Math.floor(Math.random() * this.faceOptions.length)].value
    },
    async saveAsPng () {
      const html = this.$refs.peep.$el.outerHTML
      var context = this.$refs.canvas.getContext('2d')
      var v = await Canvg.from(context, html)
      v.render()
      this.$refs.canvas.toBlob((blob) => {
        const url = URL.createObjectURL(blob)
        const link = document.createElement('a')
        link.href = url
        link.setAttribute('download', `getpeeps-${dayjs().valueOf()}.png`)
        document.body.appendChild(link)
        link.click()
        document.body.removeChild(link)
      }, 'image/png')
    }
  }
}
</script>
